mapper.select:
SELECT A.EXAM_NO , A.EXAM_NAME , A.EXAM_DATE , A.SCHOOL , A.GRADE , B.EXAM_CATEGORY_NAME , B.REF_CATEGORY_NO , C.SUBJECT_NAME FROM TBL_EXAM A JOIN TBL_EXAM_CATEGORY B ON (A.EXAM_CATEGORY_NO = B.EXAM_CATEGORY_NO) JOIN TBL_SUBJECT_CATEGORY C ON (A.SUBJECT_NO = C.SUBJECT_NO) WHERE A.EXAM_STATUS = 'Y'
AND B.REF_CATEGORY_NO = #{ schoolExam }
AND B.REF_CATEGORY_NO = #{ hagwonExam }
AND B.EXAM_CATEGORY_NO = #{ categoryNo }
AND A.GRADE = #{ grade }
AND C.SUBJECT_NO = #{ subjectNo }

mapper.insert:
INSERT
  INTO TBL_EXAM A
(
  A.EXAM_NO
, A.EXAM_CATEGORY_NO
, A.EXAM_NAME
, A.EXAM_DATE
, A.SCHOOL
, A.GRADE
, A.SUBJECT_NO
)
VALUES
(
  SEQ_EXAM_NO.NEXTVAL
, #{ categoryNo }
, #{ name }
, #{ examDate }
, #{ school }
, #{ grade }
, #{ subjectNo }
)

mapper.update:
	UPDATE
	       TBL_EXAM A


		   A.EXAM_CATEGORY_NO = #{ categoryNo }


		 , A.EXAM_NAME = #{ name }


		 , A.EXAM_DATE = #{ examDate }


 		 , A.SCHOOL = #{ school }


		 , A.GRADE = #{ grade }


		 , A.SUBJECT_NO	= #{ subjectNo }


	 WHERE A.EXAM_NO = #{ no }

mapper.insert:
INSERT
  INTO TBL_EXAM_CATEGORY A
(
  A.EXAM_CATEGORY_NO
, A.EXAM_CATEGORY_NAME
, A.REF_CATEGORY_NO
)
VALUES
(
  SEQ_EXAM_CATEGORY_NO.NEXTVAL
, #{ name }
, #{ refCategoryNo }
)

mapper.update:
UPDATE
       TBL_EXAM_CATEGORY A
   SET A.EXAM_CATEGORY_NAME = #{ name }
 WHERE A.EXAM_CATEGORY_NO = #{ no }
