mapper.select:
SELECT A.RNUM , A.EXAM_CATEGORY_NO , A.EXAM_CATEGORY_NAME , A.REF_CATEGORY_NO , A.COLOR_NO , A.COLOR_NAME , A.TAG_CLASS_NAME , A.COLOR_CODE_HEX , A.EXAM_NO , A.EXAM_NAME , A.START_DATE , A.END_DATE , A.SCHOOL , A.DESCRIPTION , A.CLASS_CODE , A.MOCK_EXAM_GRADE_NAME , A.CLASS_NAME , A.SUBJECT_NO , A.GRADE_CODE , A.TEACHER_NO , A.SUBJECT_NAME , A.HAGWON_EXAM_GRADE_NAME FROM (SELECT ROWNUM RNUM , V.EXAM_CATEGORY_NO , V.EXAM_CATEGORY_NAME , V.REF_CATEGORY_NO , V.COLOR_NO , V.COLOR_NAME , V.TAG_CLASS_NAME , V.COLOR_CODE_HEX , V.EXAM_NO , V.EXAM_NAME , V.START_DATE , V.END_DATE , V.SCHOOL , V.DESCRIPTION , V.CLASS_CODE , V.MOCK_EXAM_GRADE_NAME , V.CLASS_NAME , V.SUBJECT_NO , V.GRADE_CODE , V.TEACHER_NO , V.SUBJECT_NAME , V.HAGWON_EXAM_GRADE_NAME FROM V_EXAM_LIST V
V.EXAM_CATEGORY_NO != 1
AND V.EXAM_CATEGORY_NO != 2
AND V.REF_CATEGORY_NO IS NULL
AND V.EXAM_CATEGORY_NO = #{ categoryNo }
AND V.CLASS_CODE = #{ classCode }
AND V.EXAM_NAME LIKE '%' || #{ examName } || '%'
) A WHERE A.RNUM BETWEEN #{ pageInfo.startRow } AND #{ pageInfo.endRow }

mapper.insert:
INSERT
  INTO TBL_EXAM A
(
  A.EXAM_NO
, A.EXAM_CATEGORY_NO
, A.EXAM_NAME
, A.START_DATE
, A.END_DATE
, A.SCHOOL
, A.GRADE_CODE
, A.CLASS_CODE
, A.DESCRIPTION
)
VALUES
(
  SEQ_EXAM_NO.NEXTVAL
, #{ categoryNo }
, #{ name }
, #{ startDate, jdbcType=DATE }
, #{ endDate, jdbcType=DATE }
, #{ school, jdbcType=VARCHAR }
, #{ gradeCode, jdbcType=VARCHAR }
, #{ classCode, jdbcType=VARCHAR }
, #{ description, jdbcType=VARCHAR }
)

INSERT INTO TBL_EXAM_CATEGORY A ( A.EXAM_CATEGORY_NO , A.EXAM_CATEGORY_NAME , A.REF_CATEGORY_NO
, A.COLOR_NO
) VALUES ( SEQ_EXAM_CATEGORY_NO.NEXTVAL , #{ name } , 3
, #{ colorNo }
)

mapper.update:
UPDATE
       TBL_EXAM_CATEGORY A


	   A.EXAM_CATEGORY_NAME = #{ name },


	   A.COLOR_NO = #{ colorNo }


 WHERE A.EXAM_CATEGORY_NO = #{ no }
