mapper.select:
SELECT A.EXAM_NO , A.EXAM_NAME , A.START_DATE , A.END_DATE , A.SCHOOL , A.DESCRIPTION , B.EXAM_CATEGORY_NO , B.EXAM_CATEGORY_NAME , C.EXAM_CATEGORY_NO AS REF_EXAM_CATEGORY_NO , C.EXAM_CATEGORY_NAME AS REF_EXAM_CATEGORY_NAME , D.COLOR_NO , D.COLOR_NAME , D.COLOR_CODE_HEX , E.GRADE_CODE , E.GRADE_NAME , F.CLASS_CODE , F.CLASS_NAME , F.TEACHER_NO , G.SUBJECT_NO , G.SUBJECT_NAME , H.GRADE_CODE AS CLASS_GRADE_CODE , H.GRADE_NAME AS CLASS_GRADE_NAME FROM TBL_EXAM A LEFT JOIN TBL_EXAM_CATEGORY B ON (A.EXAM_CATEGORY_NO = B.EXAM_CATEGORY_NO) LEFT JOIN TBL_EXAM_CATEGORY C ON (B.REF_CATEGORY_NO = C.EXAM_CATEGORY_NO) LEFT JOIN TBL_COLOR D ON (B.COLOR_NO = D.COLOR_NO) LEFT JOIN TBL_GRADE E ON (A.GRADE_CODE = E.GRADE_CODE) LEFT JOIN TBL_CLASS F ON (A.CLASS_CODE = F.CLASS_CODE) LEFT JOIN TBL_SUBJECT_CATEGORY G ON (F.SUBJECT_NO = G.SUBJECT_NO) LEFT JOIN TBL_GRADE H ON (F.GRADE_CODE = H.GRADE_CODE) WHERE A.EXAM_STATUS = 'Y'
AND B.EXAM_CATEGORY_NO = #{ schoolExam }
AND B.EXAM_CATEGORY_NO = #{ mockExam }
AND B.REF_CATEGORY_NO = #{ hagwonExam }
AND B.EXAM_CATEGORY_NO = #{ categoryNo }
AND (A.GRADE_CODE = #{ gradeCode } OR H.GRADE_CODE = #{ gradeCode })
AND G.SUBJECT_NO = #{ subjectNo }
ORDER BY START_DATE DESC

mapper.insert:
INSERT
  INTO TBL_EXAM A
(
  A.EXAM_NO
, A.EXAM_CATEGORY_NO
, A.EXAM_NAME
, A.START_DATE
, A.END_DATE
, A.SCHOOL
, A.GRADE_CODE
, A.CLASS_CODE
, A.DESCRIPTION
)
VALUES
(
  SEQ_EXAM_NO.NEXTVAL
, #{ categoryNo }
, #{ name }
, #{ startDate, jdbcType=DATE }
, #{ endDate, jdbcType=DATE }
, #{ school, jdbcType=VARCHAR }
, #{ gradeCode, jdbcType=VARCHAR }
, #{ classCode, jdbcType=VARCHAR }
, #{ description, jdbcType=VARCHAR }
)

INSERT INTO TBL_EXAM_CATEGORY A ( A.EXAM_CATEGORY_NO , A.EXAM_CATEGORY_NAME , A.REF_CATEGORY_NO
, A.COLOR_NO
) VALUES ( SEQ_EXAM_CATEGORY_NO.NEXTVAL , #{ name } , 3
, #{ colorNo }
)

mapper.update:
UPDATE
       TBL_EXAM_CATEGORY A


	   A.EXAM_CATEGORY_NAME = #{ name },


	   A.COLOR_NO = #{ colorNo }


 WHERE A.EXAM_CATEGORY_NO = #{ no }
