mapper.insert:
INSERT
  INTO TBL_EXAM A
(
  A.EXAM_NO
, A.CATEGORY_NO
, A.EXAM_NAME
, A.EXAM_DATE
, A.SCHOOL
, A.GRADE
, A.SUBJECT_NO
)
VALUES
(
  SEQ_EXAM_NO.NEXTVAL
, #{ categoryNo }
, #{ name }
, #{ examDate }
, #{ school }
, #{ grade }
, #{ subjectNo }
)

mapper.select:
SELECT A.EXAM_NO , A.EXAM_NAME , A.EXAM_DATE , A.SCHOOL , A.GRADE , B.EXAM_NAME , B.REF_CATEGORY_NO , C.SUBJECT_NAME FROM TBL_EXAM A JOIN TBL_EXAM_CATEGORY B ON (A.CATEGORY_NO = B.CATEGORY_NO) JOIN TBL_SUBJECT_CATEGORY C ON (A.SUBJECT_NO = C.SUBJECT_NO) WHERE A.EXAM_STATUS = 'Y'
AND B.REF_CATEGORY_NO = #{ refCategory }
AND A.EXAM_NAME LIKE '%' || #{ name } || '%'
AND A.EXAM_DATE >= #{ beginDate }
AND #{ endDate } >= A.EXAM_DATE
AND A.GRADE = #{ grade }
AND C.CATEGORY_NO = #{ subject }

mapper.update:
	UPDATE
	       TBL_EXAM A


		   A.CATEGORY_NO = #{ categoryNo }


		 , A.EXAM_NAME = #{ name }


		 , A.EXAM_DATE = #{ examDate }


 		 , A.SCHOOL = #{ school }


		 , A.GRADE = #{ grade }


		 , A.SUBJECT_NO	= #{ subjectNo }


	 WHERE A.EXAM_NO = #{ no }
